CustomField.formula:
IF(AND( NOT(ISBLANK(ShippingCountry)), CONTAINS("USA:U.S.A:US:U.S:United States", UPPER(ShippingCountry))), "United States", 
	IF(AND( NOT(ISBLANK(ShippingCountry)), CONTAINS("CAN:CA:Canada", UPPER(ShippingCountry))), "Canada", 
			IF( NOT(ISBLANK(ShippingCountry)), ShippingCountry,
				IF( AND( ISBLANK(ShippingCountry), NOT(ISBLANK(ShippingPostalCode)), OR(CONTAINS(UPPER(TRIM(ShippingPostalCode)), 'A'), CONTAINS(UPPER(TRIM(ShippingPostalCode)), 'B'), CONTAINS(UPPER(TRIM(ShippingPostalCode)), 'C'), CONTAINS(UPPER(TRIM(ShippingPostalCode)), 'D'), CONTAINS(UPPER(TRIM(ShippingPostalCode)), 'E'), CONTAINS(UPPER(TRIM(ShippingPostalCode)), 'F'), CONTAINS(UPPER(TRIM(ShippingPostalCode)), 'G'), CONTAINS(UPPER(TRIM(ShippingPostalCode)), 'H'), CONTAINS(UPPER(TRIM(ShippingPostalCode)), 'I'), CONTAINS(UPPER(TRIM(ShippingPostalCode)), 'J'), CONTAINS(UPPER(TRIM(ShippingPostalCode)), 'K'), CONTAINS(UPPER(TRIM(ShippingPostalCode)), 'L'), CONTAINS(UPPER(TRIM(ShippingPostalCode)), 'M'), CONTAINS(UPPER(TRIM(ShippingPostalCode)), 'N'), CONTAINS(UPPER(TRIM(ShippingPostalCode)), 'O'), CONTAINS(UPPER(TRIM(ShippingPostalCode)), 'P'), CONTAINS(UPPER(TRIM(ShippingPostalCode)), 'Q'), CONTAINS(UPPER(TRIM(ShippingPostalCode)), 'R'), CONTAINS(UPPER(TRIM(ShippingPostalCode)), 'S'), CONTAINS(UPPER(TRIM(ShippingPostalCode)), 'T'), CONTAINS(UPPER(TRIM(ShippingPostalCode)), 'U'), CONTAINS(UPPER(TRIM(ShippingPostalCode)), 'V'), CONTAINS(UPPER(TRIM(ShippingPostalCode)), 'W'), CONTAINS(UPPER(TRIM(ShippingPostalCode)), 'X'), CONTAINS(UPPER(TRIM(ShippingPostalCode)), 'Y'), CONTAINS(UPPER(TRIM(ShippingPostalCode)), 'Z') )), "Canada", "United States"
				)
			)
	)
)
